mapper.update:
UPDATE board_write
SET writer = #{writer},
	title = #{title},
    content = #{content}
WHERE no = #{no}

UPDATE files
SET filename = #{fileName},
	extendedname = #{extendedName},
	filesize = #{fileSize}
WHERE no = #{no}

UPDATE board_write
SET view = view + 1
WHERE no = #{no}

UPDATE board_answer
SET view = view + 1
WHERE answerno = #{answerNo}

mapper.select:
SELECT filename
FROM files
WHERE extendedname = #{extendedName}

mapper.update:
UPDATE board_write
SET isanswered = 1
WHERE no = #{no}
